(210, '_transient_dash_4077549d03da2e451c8b5f002294ff51', '<div class="rss-widget"><ul><li><a class=''rsswidget'' href=''https://wordpress.org/news/2015/05/wordpress-4-2-2/''>WordPress 4.2.2 Security and Maintenance Release</a> <span class="rss-date">May 7, 2015</span><div class="rssSummary">WordPress 4.2.2 is now available. This is a critical security release for all previous versions and we strongly encourage you to update your sites immediately. Version 4.2.2 addresses two security issues: The Genericons icon font package, which is used in a number of popular themes and plugins, contained an HTML file vulnerable to a cross-site [&hellip;]</div></li></ul></div><div class="rss-widget"><ul><li><a class=''rsswidget'' href=''http://wptavern.com/the-recommended-hosting-page-on-wordpress-org-starts-over-from-scratch''>WPTavern: The Recommended Hosting Page on WordPress.org Starts Over From Scratch</a></li><li><a class=''rsswidget'' href=''http://wptavern.com/vaultpress-comes-out-on-top-in-recent-survey-of-wordpress-backup-tools''>WPTavern: VaultPress Comes Out on Top in Recent Survey of WordPress Backup Tools</a></li><li><a class=''rsswidget'' href=''http://wptavern.com/theme-hybrid-experiments-with-free-signups-for-club-membership''>WPTavern: Theme Hybrid Experiments with Free Signups for Club Membership</a></li></ul></div><div class="rss-widget"><ul><li class=''dashboard-news-plugin''><span>Popular Plugin:</span> <a href=''https://wordpress.org/plugins/photo-gallery/'' class=''dashboard-news-plugin-link''>Photo Gallery</a>&nbsp;<span>(<a href=''plugin-install.php?tab=plugin-information&amp;plugin=photo-gallery&amp;_wpnonce=a8188dab2a&amp;TB_iframe=true&amp;width=600&amp;height=800'' class=''thickbox'' title=''Photo Gallery''>Install</a>)</span></li></ul></div>', 'no'),
(213, '_site_transient_update_plugins', 'O:8:"stdClass":4:{s:12:"last_checked";i:1435638406;s:8:"response";a:1:{s:19:"akismet/akismet.php";O:8:"stdClass":6:{s:2:"id";s:2:"15";s:4:"slug";s:7:"akismet";s:6:"plugin";s:19:"akismet/akismet.php";s:11:"new_version";s:5:"3.1.2";s:3:"url";s:38:"https://wordpress.org/plugins/akismet/";s:7:"package";s:56:"https://downloads.wordpress.org/plugin/akismet.3.1.2.zip";}}s:12:"translations";a:0:{}s:9:"no_update";a:2:{s:43:"custom-post-type-ui/custom-post-type-ui.php";O:8:"stdClass":7:{s:2:"id";s:5:"13183";s:4:"slug";s:19:"custom-post-type-ui";s:6:"plugin";s:43:"custom-post-type-ui/custom-post-type-ui.php";s:11:"new_version";s:5:"1.1.1";s:3:"url";s:50:"https://wordpress.org/plugins/custom-post-type-ui/";s:7:"package";s:68:"https://downloads.wordpress.org/plugin/custom-post-type-ui.1.1.1.zip";s:14:"upgrade_notice";s:79:"Re-add post type and taxonomy select buttons and only display for non-js users.";}s:9:"hello.php";O:8:"stdClass":6:{s:2:"id";s:4:"3564";s:4:"slug";s:11:"hello-dolly";s:6:"plugin";s:9:"hello.php";s:11:"new_version";s:3:"1.6";s:3:"url";s:42:"https://wordpress.org/plugins/hello-dolly/";s:7:"package";s:58:"https://downloads.wordpress.org/plugin/hello-dolly.1.6.zip";}}}', 'yes'),
